import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CreateIssueForm = () => {
  const [project, setProject] = useState('');
  const [issueType, setIssueType] = useState('');
  const [status, setStatus] = useState('');
  const [summary, setSummary] = useState('');
  const [assignee, setAssignee] = useState('');
  const [labels, setLabels] = useState('');
  const [sprint, setSprint] = useState('');
  const [attachment, setAttachment] = useState(null);
  const [startDate, setStartDate] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [assigneeOptions, setAssigneeOptions] = useState([]);


  useEffect(() => {
    // Fetch team members from backend
    axios.get('/api/get-team-members/')
      .then(response => {
        setAssigneeOptions(response.data.users);
      })
      .catch(error => {
        console.error('Error fetching team members:', error);
      });
  }, []);


  const handleSubmit = (e) => {
    e.preventDefault();
    // Your submission logic here
  };

  const handleAttachmentChange = (e) => {
    setAttachment(e.target.files[0]);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Project:</label>
        <textarea
          value={project}
          onChange={(e) => setProject(e.target.value)}
        />
      </div>
      <div>
        <label>Issue Type:</label>
        <select value={issueType} onChange={(e) => setIssueType(e.target.value)}>
          <option value="">Select...</option>
          <option value="Story">Story</option>
          <option value="Task">Task</option>
          <option value="Bug">Bug</option>
          <option value="Epic">Epic</option>
        </select>
      </div>
      <div>
        <label>Status:</label>
        <select value={status} onChange={(e) => setStatus(e.target.value)}>
          <option value="">Select...</option>
          <option value="To-Do">To-Do</option>
          <option value="In-Progress">In-Progress</option>
          <option value="Done">Done</option>
        </select>
      </div>
      <div>
        <label>Summary:</label>
        <textarea
          value={summary}
          onChange={(e) => setSummary(e.target.value)}
        />
      </div>
      <div>
        <label>Assignee:</label>
        <select
          value={assignee}
          onChange={(e) => setAssignee(e.target.value)}
        >
          <option value="">Select...</option>
          {assigneeOptions.map(user => (
            <option key={user.email} value={user.email}>
              {`${user.first_name} ${user.last_name}`}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label>Labels:</label>
        <select value={labels} onChange={(e) => setLabels(e.target.value)}>
          <option value="">Select...</option>
          {/* Add label options here */}
        </select>
      </div>
      <div>
        <label>Sprint:</label>
        <select value={sprint} onChange={(e) => setSprint(e.target.value)}>
          <option value="">Select...</option>
          {/* Add sprint options here */}
        </select>
      </div>
      {issueType === "Epic" && (
        <div>
          <label>Start Date:</label>
          <input
            type="datetime-local"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
        </div>
      )}
      {issueType === "Epic" && (
        <div>
          <label>Due Date:</label>
          <input
            type="datetime-local"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
          />
        </div>
      )}
      <div>
        <label>Attachment:</label>
        <input type="file" onChange={handleAttachmentChange} />
      </div>
      <div>
        <button type="submit">Create</button>
        <button type="button">Cancel</button>
      </div>
    </form>
  );
};

export default CreateIssueForm;
