import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Add_team_members = () => {
    const [email, setEmail] = useState('');
    const [csrfToken, setCsrfToken] = useState('');

    useEffect(() => {
       
        const fetchCsrfToken = async () => {
            try {
                const response = await axios.get('http://localhost:8000/djapp/csrf-token/');
                console.log(csrf)
                setCsrfToken(response.data.csrfToken);
            } catch (error) {
                console.error('Error fetching CSRF token:', error);
            }
        };

        fetchCsrfToken(); // Call the fetchCsrfToken function
    }, []); // Empty dependency array ensures this effect runs only once

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Include CSRF token in request headers
            const headers = {
                'X-CSRFToken': csrfToken,
                'Content-Type': 'application/json',
            };

            // Make POST request with CSRF token included in headers
            const response = await axios.post('http://localhost:8000/djapp/generate_invitation_token/', { email:email }, { headers });
            console.log(response.data);
          
            setEmail('');
        } catch (error) {
            console.error('Error sending invitation email:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="email">Enter Email:</label>
            <input
                type="email"
                id="email"
                value={email}
                onChange={handleEmailChange}
                required
            />
            <button type="submit">Send Invitation</button>
        </form>
    );
};

export default Add_team_members;
