import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const AcceptInvitation = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const token = searchParams.get('token');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleAcceptInvitation = () => {
    setIsLoading(true);
    axios.post('http://localhost:8000/djapp/verify_invitation_token/', { token })
      .then(response => {
        console.log('Invitation accepted:', response.data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error accepting invitation:', error);
        setError(error);
        setIsLoading(false);
      });
  };

  return (
    <div>
      <h1>Accept Invitation Page</h1>
      <p>Token: {token}</p>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <button onClick={handleAcceptInvitation} disabled={!token}>
          Accept Invitation
        </button>
      )}
      {error && <p>Error: {error.message}</p>}
      {/* You can add more UI elements here */}
    </div>
  );
};

export default AcceptInvitation;
