import React, { useState } from 'react';
import { Link, Navigate } from 'react-router-dom';
import { connect } from 'react-redux';
import { login } from '../actions/auth';
import axios from 'axios';
import './css/login.css';

const Login = ({ login, isAuthenticated }) => {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });

    const { email, password } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();

        login(email, password);
    };

    const continueWithGoogle = async () => {
        try {
            const res = await axios.get(`${process.env.REACT_APP_API_URL}/auth/o/google-oauth2/?redirect_uri=http://localhost:8000`)

            window.location.replace(res.data.authorization_url);
        } catch (err) {

        }
    };

    if (isAuthenticated) {
        return <Navigate to='/project' />
    }

    return (
        <div className='login-container'>
            <p className='login-text'>Not a M</p>

            <h1 className='login-title'>Hello Again!</h1>
            {/* <p className='login-text'>Sign into your Account</p> */}
            <form onSubmit={e => onSubmit(e)}>
                <div className='login-form-group'>
                    <input
                        className='login-form-control'
                        type='email'
                        placeholder='Email'
                        name='email'
                        value={email}
                        onChange={e => onChange(e)}
                        required
                    />
                </div>
                <div className='login-form-group'>
                    <input
                        className='login-form-control'
                        type='password'
                        placeholder='Password'
                        name='password'
                        value={password}
                        onChange={e => onChange(e)}
                        minLength='6'
                        required
                    />
                </div>
                <button className='login-btn' type='submit'>Login</button>
            </form>
            <button className='login-google-btn' onClick={continueWithGoogle}>
                Continue With Google
            </button>

            <div className='login-links'>
                <p>
                    Don't have an account? <Link to='/signup'>Sign Up</Link>
                </p>
                <p>
                    Forgot your Password? <Link to='/reset_password'>Reset Password</Link>
                </p>
            </div>
        </div>
    );
};

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps, { login })(Login);
